mapper.update:
UPDATE t_user
SET user_state = 0
WHERE user_id = #{userId}

UPDATE t_user
SET user_name = #{user.userName}, password = #{user.password},user_state = #{user.userState}
WHERE user_id = #{user.userId}

mapper.select:
SELECT count(1)
FROM t_user
WHERE user_name = #{userName}
AND password = #{password}
AND user_state = 1
